        UninstallStack()
        {
            $Apps            = "\Wow6432Node","" | % { 

                               Get-ChildItem "HKLM:\Software$_\Microsoft\Windows\CurrentVersion\Uninstall" 

                               } | Get-ItemProperty
            $Edge            = $Apps | ? DisplayName -match "(^Microsoft Edge$)"
